type Provider = "mysql" | "mariadb" | "postgres" | "mssql" | "db2" | "sqlite" | "oracle";
interface Metadata {
    tableName: string;
    columnName: string;
    dataType: string;
}
declare function getMetadata(sequelize: Sequelize, provider: Provider, schema?: string): Promise<string>;

export { type Metadata, type Provider, getMetadata };
